'use strict';
/* global fetch */

var baseUrl = 'https://min-api.cryptocompare.com/data/';
var apiKey = '';

function setApiKey(userApiKey) {
  apiKey = userApiKey;
}

function fetchJSON(url) {
  if (apiKey !== '') {
    if (url.indexOf('?') > -1) {
      url += '&api_key=';
    } else {
      url += '?api_key=';
    }

    url += apiKey;
  }

  return fetch(url).then(function (res) {
    if (!res.ok) {
      throw new Error("".concat(res.status, " ").concat(res.statusText));
    }

    return res.json();
  }).then(function (body) {
    if (body.Response === 'Error') throw body.Message;
    return body;
  });
}

