import React from 'react';
import _ from 'lodash';
const cc = require('cryptocompare')

const MAX_FAVORTITES = 10;
export const Appcontext = React.createContext();

export class AppProvider extends React.Component {
    constructor(props){   
        super(props);
        this.state = {
            page: 'settings',
            favorites:[],
            ...this.savedSetting(),
            setPage: this.setPage,
            addCoin: this.addCoin,
            removeCoin: this.removeCoin,
            isInFavorites: this.isInFavorites,
            filteredCoins: this.filteredCoins,
            confirmFavorites:this.confirmFavorites,
            setFilteredCoins: this.setFilteredCoins,
            setCurrentFavorite: this.setCurrentFavorite
        }
    }

    componentDidMount = ()=>{
        this.fetchCoins();
        this.fetchPrices();
        this.fetchHistoric();
        console.log('Component does mount');
    }

    componentDidUpdate = ()=>{
        console.log('Yes, looks like the component did update!!')
    }

    componentDidCatch = ()=>{
        console.log('There you go! I caught it!');
    }


    addCoin = key =>{
        let favorites = [...this.state.favorites]
        if(favorites.length <= MAX_FAVORTITES){
            favorites.push(key);
            this.setState({favorites})
        }
    }
    removeCoin = key =>{
        let favorites = [...this.state.favorites];
        this.setState({favorites: _.pull(favorites,key)})
    }

    isInFavorites = key => _.includes(this.state.favorites,key)

    fetchCoins = async ()=>{
        let coinList = (await cc.coinList()).Data;
        this.setState({coinList})
        console.log(coinList);
    }

    fetchPrices = async  ()=>{
        if(this.state.firstVisit) return;
        let prices = await this.prices();
        console.log(prices);
        this.setState({prices})
    }

    prices = async ()=>{
        let returnData = [];
        for(let i=0;i<this.state.favorites.length;i++){
            try{
                let pricesData = await cc.priceFull(this.state.favorites[i],'USD')
                returnData.push(pricesData)
            } catch(e){
                console.warn('Fetch price error:',e)
            }
        }
        return returnData;
    } 

    confirmFavorites=()=>{
        let currentFavourites = this.state.favorites[0];
        console.log("Settings an item")
        this.setState({
            firstVisit: false,
            page: 'dashboard',
            currentFavourites,
        },()=>{
            this.fetchPrices()
        })
        console.log('is it happening')
        localStorage.setItem('cryptoDash',JSON.stringify({
            favorites: this.state.favorites,
            currentFavourites   
        }))
    }

    setCurrentFavorite = (sym)=>{
        this.setState({
            currentFavourites: sym
        })
        localStorage.setItem('cryptoDash',JSON.stringify({
            ...JSON.parse(localStorage.getItem('cryptoDash')),
            currentFavourites:sym
        }))
    }

    savedSetting(){

        let cryptoDashData = JSON.parse(localStorage.getItem('cryptoDash'));
        if(!cryptoDashData){ 
            return { firstVisit: true,
                page: 'settings',
                currentFavourites
            }    
        } 

        let {favorites,currentFavourites}  = cryptoDashData;
        console.log('Saved current FAv?')
            return {favorites,currentFavourites}
    }

    setFilteredCoins = (filteredCoins) => this.setState({filteredCoins})

    setPage = page => {
        console.log("setting page" + page)   
        this.setState({page})

    }

    render() {
        return (
            <Appcontext.Provider value={this.state}>
            {this.props.children}
            </Appcontext.Provider>  
        )
    }
}
